INSERT INTO monumento (codigo_pais, nombre_pais, nombre_ciudad, localizacion, nombre_monumento, descripcion, url_foto) VALUES ( 'ES', 'España', 'Barcelona', '41.4036, 2.1744', 'Sagrada Familia', 'Basílica diseñada por Antoni Gaudí, obra maestra del modernismo catalán iniciada en 1882 y aún en construcción.', 'https://upload.wikimedia.org/wikipedia/commons/thumb/5/50/Sagrada_Familia_8-12-21_%281%29.jpg/800px-Sagrada_Familia_8-12-21_%281%29.jpg');
INSERT INTO monumento (codigo_pais, nombre_pais, nombre_ciudad, localizacion, nombre_monumento, descripcion, url_foto) VALUES ( 'FR', 'Francia', 'París', '48.8584, 2.2945', 'Torre Eiffel', 'Estructura de hierro construida en 1889 para la Exposición Universal, símbolo icónico de París con 330 metros de altura.', 'https://upload.wikimedia.org/wikipedia/commons/thumb/a/a8/Tour_Eiffel_Wikimedia_Commons.jpg/800px-Tour_Eiffel_Wikimedia_Commons.jpg');
INSERT INTO monumento (codigo_pais, nombre_pais, nombre_ciudad, localizacion, nombre_monumento, descripcion, url_foto) VALUES ( 'IT', 'Italia', 'Roma', '41.8902, 12.4922', 'Coliseo Romano', 'Anfiteatro construido en el siglo I d.C., el mayor anfiteatro jamás construido, donde se celebraban combates de gladiadores.', 'https://upload.wikimedia.org/wikipedia/commons/thumb/d/de/Colosseo_2020.jpg/800px-Colosseo_2020.jpg');
INSERT INTO monumento (codigo_pais, nombre_pais, nombre_ciudad, localizacion, nombre_monumento, descripcion, url_foto) VALUES ( 'GB', 'Reino Unido', 'Londres', '51.5007, -0.1246', 'Big Ben', 'Icónica torre del reloj del Palacio de Westminster, construida en 1859, uno de los símbolos más reconocibles de Londres.', 'https://upload.wikimedia.org/wikipedia/commons/thumb/9/93/Clock_Tower_-_Palace_of_Westminster%2C_London_-_May_2007.jpg/800px-Clock_Tower_-_Palace_of_Westminster%2C_London_-_May_2007.jpg');
