INSERT INTO monumento (codigo_pais, nombre_pais, nombre_ciudad, localizacion, nombre_monumento, descripcion, url_foto) VALUES ('US', 'Estados Unidos', 'Nueva York', '40.6892, -74.0445', 'Estatua de la Libertad', 'Escultura neoclásica en Liberty Island, regalo de Francia en 1886, símbolo de libertad y democracia con 93 metros de altura.', 'https://upload.wikimedia.org/wikipedia/commons/thumb/a/a1/Statue_of_Liberty_7.jpg/800px-Statue_of_Liberty_7.jpg');
