INSERT INTO monumento (codigo_pais, nombre_pais, nombre_ciudad, localizacion, nombre_monumento, descripcion, url_foto) VALUES ( 'CN', 'China', 'Beijing', '40.4319, 116.5704', 'Gran Muralla China', 'Sistema de fortificaciones construido entre el siglo V a.C. y siglo XVI, con más de 21.000 km de longitud total.', 'https://upload.wikimedia.org/wikipedia/commons/thumb/2/23/The_Great_Wall_of_China_at_Jinshanling-edit.jpg/800px-The_Great_Wall_of_China_at_Jinshanling-edit.jpg');
INSERT INTO monumento (codigo_pais, nombre_pais, nombre_ciudad, localizacion, nombre_monumento, descripcion, url_foto) VALUES ( 'BR', 'Brasil', 'Río de Janeiro', '-22.9519, -43.2105', 'Cristo Redentor', 'Estatua Art Déco de Jesús de 30 metros de altura inaugurada en 1931, situada en la cima del cerro del Corcovado.', 'https://upload.wikimedia.org/wikipedia/commons/thumb/4/4f/Christ_the_Redeemer_-_Cristo_Redentor.jpg/800px-Christ_the_Redeemer_-_Cristo_Redentor.jpg');
